import mark from './icons/mark.png'
import L from 'leaflet';

export default class Marks extends Component{

    state = {
        prop: []
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {

        let Icon = L.icon({
            iconUrl: mark,
            iconRetinaUrl: mark,
            iconAnchor: [5, 55],
            popupAnchor: [10, -44],
            iconSize: [30, 35],
        });

        const { position } = this.props
