                Data.map(post => {
                    if (post.region == "los lagos") {
                        return (
                            <Marker key={post.id} position={[post.lat, post.long]} icon={Icon}>
                                <>
                                    <Popup>
                                        {post.tittle}
                                    </Popup>
                                </>
                            </Marker>


                        )
                    }
                })
                }
                </div>

        )
    }
}
