import React, { Component} from 'react'
import './css/Home.css'
import { Grid, Card} from '@material-ui/core'
import RegionesChile from '../containers/ContainerHome'
import Cards from '../components/Cards/Cards'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import axios from 'axios'

export default class Home extends Component {

    state = {
        prop: [],
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data
                this.setState({ prop});
            })
    }
    render() {
        return (
            <>
                <Grid container>
                    <Card className='main1'>
                        <h1 className='paper_prim'>Propiedades por Regiones</h1>
                        <RegionesChile />

                    </Card>
                    <Card className='main2'>
                        <h2 className='paper_prim'>Propiedades destacadas en Venta</h2>
                        <div>

                        </div>

                        <Carousel>
                            {this.state.prop.map((item) => {
                                return (
                                    <div key={item.id}>
                                        <Cards
                                            idCard={item.id}
                                            imgBannerCard={item.imgBanner}
                                            tittleCard={item.tittle}
                                            priceCard={item.price}
                                            smallContentCard={item.smallContent}
                                        />
                                    </div>
                                )
                            })}

                        </Carousel>
                    </Card>
                </Grid>
            </>
        )
    }
}
