import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import mark from './icons/mark.png'
import L from 'leaflet';
import axios from 'axios'
import { Link } from "react-router-dom";
import "./Map.css"

import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';



export default class MapTest extends Component {
    constructor(props) {
        super(props);
        this.state = {
            prop: [],
            value: 'Todo'
        };
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    handleChange = event => {
        this.setState({ value: event.target.value });
    };

    render() {

        let Icon = L.icon({
            iconUrl: mark,
            iconRetinaUrl: mark,
            iconAnchor: [5, 55],
            popupAnchor: [10, -44],
            iconSize: [30, 35],
        });
        const { position } = this.props
        const { zoom } = this.props
        const { idParams } = this.props
        const { prop } = this.state
        const { value } = this.state

        return (
            <div>

                <label>
                    Filtrar por Categor�a:
                    <select value={value} onChange={this.handleChange}>
                        <MenuItem value="Todo">Todo</MenuItem>
                        <MenuItem value="1">Venta</MenuItem>
                        <MenuItem value="2">Arriendo</MenuItem>
                    </select>
                </label>

                <MapContainer
                    className="map"
                    center={position}
                    zoom={zoom}
                    style={{ height: this.props.height}}
                >
                    <TileLayer
                        attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />

                    {prop.map((item) => {
                        if (idParams === item.region) {
                            if (item.category === value) {
                                return (
                                    <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                        <Popup>
                                            <li>{item.tittle}</li>
                                            <Link to={"/propiedad/" + item.id}>
                                                Ir a la Propiedad
                                        </Link>
                                        </Popup>
                                    </Marker>
                                )
                            }
                            if ("Todo" === value) {
                                return (
                                    <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                        <Popup>
                                            <li>{item.tittle}</li>
                                            <Link to={"/propiedad/" + item.id}>
                                                Ir a la Propiedad
                                        </Link>
                                        </Popup>
                                    </Marker>
                                )
                            }
                        }
                    })}
                </MapContainer>
            </div>
        );
    }
}
