import React, { Component } from 'react';
import axios from 'axios'
import Map from './Map'
import FormContact from './FormContact/FormContact'

export default class Axios extends Component {
    state = {
        prop: [],
        id: '',
        tittle: '',
        price:'',
        smallContent: '',
        coordenadasP: []
    }

    componentDidMount() {
            axios.get("http://localhost:3001/propiedades/" + this.props.value)
            .then(res => {
                const prop = res.data
                this.setState(
                    {
                        id: prop.id,
                        tittle: prop.tittle,
                        price: prop.price,
                        coordenadasP: prop.coordenadasP
                    }
                )
            })    
    }


    render() {
        const { id } = this.state
        const { tittle } = this.state
        const { coordenadasP } = this.state
        const coordenadas = (coordenadasP[0] + "," + coordenadasP[1])
        const zoomP = 13
        const heightP = 300
        const prefijo = "PROPIEDAD"
        const text = "Consultar por la Propiedad"

        console.log(this.state.id);
        return (
            <>
                <h1>{id} </h1>
                <h2>{this.props.tittle}</h2>
                <h3>{this.props.price}</h3>
                <p>{coordenadas}</p>
                <Map
                    position={coordenadasP[0],coordenadasP[1]}
                    zoom={zoomP}
                    popup={tittle}
                    height={heightP}
                />
                <FormContact
                    asunto={tittle}
                    pref={prefijo}
                    encabezado={text}
                />
            </>
        )
    }
}
