import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import icon from 'leaflet/dist/images/marker-icon.png';
import L from 'leaflet';

export default class Map extends Component {

    state = {
        position: [0,0]
    }

    componentDidUpdate(prevProps) {
        if (this.props.pos !== prevProps.pos) {
            this.setState(this.props.pos);
        }
    }


    render() {

        let Icon = L.icon({
            iconUrl: icon,
            shadowUrl: iconShadow
        });

        const positi = [-41.459371, -72.959090]
        const positin = [0, 0]

        const { zoom } = this.props
        const { popup } = this.props
        const { pos } = this.props


        console.log("POS-STATE: " + pos)

        return (
            <>
                <MapContainer center={pos} zoom={13} scrollWheelZoom={false} style={{ height: this.props.height }}>
                    <TileLayer
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={pos} icon={Icon}>
                        <Popup>
                            A pretty CSS3 popup. <br /> Easily customizable.
                        </Popup>
                    </Marker>
                </MapContainer>

            </>
        );
    }
}
