import React, { Component } from 'react';
import { Grid } from '@material-ui/core';
import axios from 'axios'
import Map from '../components/Map'
import FormContact from "../components/FormContact/FormContact";

const { match } = this.props

export default class Propiedad extends Component {
    state = {
        prop: []
    }



    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data.filter(item => item.id.includes(match.params.id));
                this.setState({ prop });
            })
    }

    render() {
        const { prop } = this.state
        const { match } = this.props
        const zoomP = 13
        const heightP = 300
        const prefijo = "PROPIEDAD"
        const text = "Consultar por la Propiedad"

        return (
            <div>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                    </Grid>  
                    <Grid item xs={6}>
                        {prop.map(item => {
                                return (
                                    <div key={item.id}>
                                        <h1>
                                            {item.tittle}
                                        </h1>
                                        <Map
                                            position={item.coordenadasP}
                                            zoom={zoomP}
                                            popup={item.tittle}
                                            height={heightP}
                                        />
                                        <FormContact
                                            asunto={item.tittle}
                                            pref={prefijo}
                                            encabezado={text}
                                        />
                                    </div>
                                )

                        })}  
                    </Grid>
                    <Grid item xs={6}>
                        <h3>CONTENIDO</h3>
                    </Grid>
                </Grid>      
            </div>
        )
    }
}
