import React, { Component, Fragment } from 'react'
import axios from 'axios'
import Cards from '../components/Cards'
import { Paper, Grid } from '@material-ui/core';
import Pagination from '../components/Pagination';

export default class Ventas extends Component {
    state = {
        prop: [],
        value: 1,
        currentOffset: 0,
        pageCounter: 1,
    }

    componentDidMount() {
        this.fetchData();
    }

    componentDidUpdate(prevProps, prevState) {
        const { currentOffset } = this.state;
        if (currentOffset !== prevState.currentOffset) {
            if (currentOffset < 0) {
                this.setState({
                    currentOffset: 0,
                    pageCounter: 1,
                });
                this.fetchData(prevState.currentOffset);
            }
            this.fetchData(currentOffset);
        }
    }


    fetchData = (offset = 0) => {
        const url = 'http://localhost:3001/propiedades';
        let params = {
            offset: offset,
            limit: 20
        }
        axios.get(url, { params })
            .then(res => {
                const { results } = res.data;

                this.setState({
                    pokeData: results,
                })

            })
            .catch(error => {
                console.log(error);
            })
    }



    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {
        const { prop } = this.state
        const { value } = this.state
        return (
            <div>
                <Fragment>
                    <Grid item xs={12}>
                        <Paper className='paper_prim'>
                            <h1>Propiedades en Venta </h1>
                        </Paper>
                    </Grid>
                    <Grid container spacing={24} >
                        {prop.map(item => {
                            if (item.category == value) {
                                return (
                                    <Cards
                                        idCard={item.id}
                                        imgBannerCard={item.imgBanner}
                                        tittleCard={item.tittle}
                                        smallContentCard={item.smallContent}
                                    />
                                )
                            }
                        })}

                    </Grid>
                </Fragment>
            </div>
        )
    }
}
