import React, { Component } from 'react';
import axios from 'axios'
import { Pie } from 'react-chartjs-2';

export default class GrafPie extends Component {
        state = {
            mat: [],
            materiales: [],
            porcentajes: [],
            colores: ["#28b012", "#48f985", "#bfcf12", "#7c2c7b", "#58f985", "#78b012", "#38b012"]
        }

    componentDidMount() {
        axios.get("http://localhost:3001/propiedades/" + this.props.value)
            .then(res => {
                const prop = res.data;
                const mat = prop.mat;
                this.setState({ mat: mat })
                const materiales = [], porcentajes = [];

                this.state.mat.map(item => {
                    materiales.push(item.material);
                    porcentajes.push(item.porcentaje);
                })

                this.setState(
                    {
                        materiales: materiales,
                        porcentajes: porcentajes
                    }
                )
            })
    }

    render() {
        console.log(this.state.mat)
        console.log(this.state.materiales)
        console.log(this.state.porcentajes)
        console.log(this.state.colores)

        const data = {
            datasets: [{
                data: this.state.porcentajes,
                backgroundColor: this.state.colores
            }],
            labels: this.state.materiales,
        };

        const opciones = {
            maintainAspectRatio: false,
            responsive: true
        }


        return (
            <>
                <div style={{ width: "100%", height: "500px" }}>
                    <h1> Pie graf</h1>
                    <Pie data={data} options={opciones} />
                </div>
            </>
        )
    }
}
