import React, { Component } from 'react';
import loslagos_1 from './img/loslagos_1.png';
import loslagos_2 from './img/loslagos_2.png';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: "Mouse Event"
        }
    }

    handleEvent = (event) => {
        if (event.type === "mousedown") {
            this.setState({ message: "Mouse Down" });
        } else {
            this.setState({ message: "Mouse Up" });
        }
    }

    render() {
        return (
            <div>
                <div className="h4 bg-secondary text-white text-center p-2">
                    {this.state.message}
                </div>
                <div className="text-center">
                    <img src={loslagos_1} onMouseDown={this.handleEvent} onMouseUp={this.handleEvent} >
                        Change Image
          </img>
                </div>
            </div>
        );
    }
}


export default Home;
