import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, LayersControl, LayerGroup, FeatureGroup, Rectangle } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios'
import { Link } from "react-router-dom";
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import icon from 'leaflet/dist/images/marker-icon.png';


let Icon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});


export default class MapReg extends Component {
    state = {
        prop: []
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {

        const { position } = this.props
        const { zoom } = this.props
        const { idParams } = this.props
        const { value } = this.props

        return (
            <div>
                <MapContainer
                    className="map"
                    center={position}
                    zoom={zoom}
                    style={{ height: this.props.height }}
                >

                    <LayersControl position="topright">
                        <TileLayer
                            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />

                        {this.state.prop.map((item) => {
                            if (idParams === item.region) {
                                if (item.category === value) {
                                    return (
                                        <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                            <Popup>
                                                <li>{item.tittle}</li>
                                                <Link to={"/propiedad/" + item.id}>
                                                    Ir a la Propiedad
                                            </Link>
                                            </Popup>
                                        </Marker>
                                    )
                                }
                                if ("Todo" === value) {
                                    return (
                                        <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                            <Popup>
                                                <li>{item.tittle}</li>
                                                <Link to={"/propiedad/" + item.id}>
                                                    Ir a la Propiedad
                                            </Link>
                                            </Popup>
                                        </Marker>
                                    )
                                }
                            }
                        })}
                    </LayersControl>
                </MapContainer>
            </div>
        );
    }
}
