import React, { Component } from 'react'
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import './ListProp.css'

export default class ListProp extends Component {

    constructor(props) {
        super(props)
        this.state = {
            inactivaProp: 0,
            datosProp: [],
            orgdatosProp: [],
            cantidadPP: 11,
            paginaInicial: 0
        }
        this.handlePageClick = this.handlePageClick.bind(this);
    }

    handlePageClick = (e) => {
        const paginaSelect = e.selected;
        const inactivaProp = paginaSelect * this.state.cantidadPP;
        this.setState({
            paginaInicial: paginaSelect,
            inactivaProp: inactivaProp
        }, () => {
            this.siguientesProp()
        });
    };

    siguientesProp() {
        const data = this.state.orgdatosProp;
        const rango = data.slice(this.state.inactivaProp, this.state.inactivaProp + this.state.cantidadPP)
        this.setState({
            pageCount: Math.ceil(data.length / this.state.cantidadPP),
            datosProp: rango
        })
    }

    componentDidMount() {
        this.getProp();
    }

    getProp() {
        axios.get(`https://jsonplaceholder.typicode.com/comments`)
            .then(res => {
                const prop = res.data;
                const rango = prop.slice(this.state.inactivaProp, this.state.inactivaProp + this.state.cantidadPP)

                this.setState({
                    pageCount: Math.ceil(prop.length / this.state.cantidadPP),
                    orgdatosProp: res.data,
                    datosProp: rango
                })
            });
    }

    render() {
        return (
            <div>
                <h1>Welcome to GKTechy!</h1>

                <table border="1">
                    <thead>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Body</th>

                    </thead>
                    <tbody>
                        {
                            this.state.datosProp.map((tdata, i) => (
                                <tr>
                                    <td>{tdata.id}</td>
                                    <td>{tdata.name}</td>
                                    <td>{tdata.email}</td>
                                    <td>{tdata.body}</td>
                                </tr>

                            ))
                        }

                    </tbody>
                </table>

                <ReactPaginate
                    previousLabel={"Anterior"}
                    nextLabel={"Siguiente"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={this.state.pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={this.handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"} />

            </div>
        )
    }
}
