import React, { Component } from 'react';
import axios from 'axios'
import './css/Propiedad.css'
import { Bar, Pie } from 'react-chartjs-2';

export default class Servicios extends Component {
    constructor(props) {
        super(props);
        this.state = {
            mat: [],
            materiales: [],
            porcentajes: [],
            colores: []
        }
    }

    componentDidMount() {
        axios.get("http://localhost:3001/propiedades/" + 0)
            .then(res => {
                const prop = res.data;
                const mat = prop.mat;
                this.setState({ mat: mat })
                const materiales = [], porcentajes = [];

                this.state.mat.map(item => {
                    materiales.push(item.material);
                    porcentajes.push(item.porcentaje);
                })

                this.setState(
                    {
                        materiales: materiales,
                        porcentajes: porcentajes
                    }
                )
            })

    }

    color() {
        const colorHEX = (Math.random() * 0xffffff * 1000000 ).toString(16)
        return "#" + colorHEX.slice(0, 6);
    }

    generarColores() {
        const colores = '';
        for (const i = 0; i < this.state.mat.length; i++) {
            colores.push(this.color());
        }
        this.setState({colores:colores})
    }


    render() {
        console.log(this.state.mat)
        console.log(this.state.materiales)
        console.log(this.state.porcentajes)
        console.log(this.state.colores)
        console.log(this.generarColores())


        {/*const data = {
            datasets: [{
                label: 'Numeros',
                backgroundColor: 'rgba(0,255,0,1)',
                borderColor: 'black',
                borderWidth: 1,
                hoverBackgroundColor: 'rgba(0,255,0,0.2)',
                hoverBorderColor: '#FF0000',
                data: this.state.porcentajes

            }],
            labels: this.state.materiales
        };*/}

        const data = {
            datasets: [{
                data: this.state.porcentajes,
                backgroundColor: this.state.colores
            }],
            labels: this.state.materiales,
        };

        const opciones = {
            maintainAspectRatio: false,
            responsive: true
        }



        return (
            <>             
            <div style={{ width: "100%", height: "500px" }}>
                <h1> Pie graf</h1>
                    <Pie data={data} options={opciones}/>
                </div>
                </>
        )
    }
}
