import React, { Component } from 'react';
import { useParams } from "react-router-dom";
import { Grid } from '@material-ui/core';
import axios from 'axios'
import Map from '../components/Map'
import FormContact from "../components/FormContact";

export default class Propiedad extends Component {
    state = {
        prop: []
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {
        const { prop } = this.state
        return (
            <div>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                    </Grid>  
                    <Grid item xs={6}>
                            <Use
                                list={prop}
                             />
                    </Grid>
                    <Grid item xs={6}>
                        <h3>CONTENIDO</h3>
                    </Grid>
                    <Grid item xs={12}>
                        <FormContact />
                    </Grid>  
                </Grid>      
            </div>
        )
    }
}

function Use(props) {
    let { id } = useParams();
    const zoomP = 13
    const heightP = 300

    return (
        <div>
             {props.list.map(item => {
                    if (id == item.id) {
                        return (
                            <div key={item.id}>
                                <h1>
                                 {item.tittle}
                                </h1>
                                <Map
                                    position={item.coordenadasP}
                                    zoom={zoomP}
                                    popup={item.tittle}
                                    height={heightP}
                                    idParams={id}
                                />                     
                            </div>
                        )
                    }
                })}     
        </div>   
    );
}
