import React, { Component, Fragment } from 'react'
import axios from 'axios'
import Cards from '../components/Cards'
import { Paper, Grid, CssBaseline, Container} from '@material-ui/core';

export default class Servicios extends Component {
    state = {
        prop: [],
        value: 1
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {
        const { prop } = this.state
        const { value } = this.state
        return (
            <div>
                <Fragment>
                        <Grid container spacing={16} justify="center">
                                {prop.map(item => {
                                    if (item.category == value) {
                                        return (
                                            <Cards
                                                idCard= {item.id}
                                                imgBannerCard = {item.imgBanner}
                                                tittleCard = {item.tittle}
                                                smallContentCard = {item.smallContent}
                                             />
                                        )
                                    }
                                })}
                           
                        </Grid>
                </Fragment>
            </div>
        )
    }
}
