import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import icon from 'leaflet/dist/images/marker-icon.png';
import L from 'leaflet';

export default class Map extends Component {

    constructor(props) {
        super(props)
        this.state = {
            poss: [],
        }

    }

    componentDidMount() {
        if (this.props.pos.length !== 0) {
            this.setState({poss: this.props.pos})
        }
    }

    render() {

        let Icon = L.icon({
            iconUrl: icon,
            shadowUrl: iconShadow
        });

        const position = [-41.459371, -72.959090]


        const { zoom } = this.props
        const { popup } = this.props

        console.log(this.props.pos)
        console.log(position)
        console.log("POS-STATE: " + this.state.poss)

        return (
            <>

                {this.props.pos.length !== 0 &&

                    <MapContainer
                        className="map"
                        center={this.props.pos}
                        zoom={zoom}
                        style={{ height: this.props.height }}
                    >
                        <TileLayer
                            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />
                        <Marker position={this.prop.pos} icon={Icon} >
                            <Popup>
                                {popup}
                            </Popup>
                        </Marker>
                    </MapContainer>
                }
            </>
        );
    }
}
