import React, { Component } from 'react';
import axios from 'axios'
import { Bar} from 'react-chartjs-2';

export default class Servicios extends Component {
    constructor(props) {
        super(props);
        this.state = {
            garf2: [],
            materiales: [],
            porcentajes: [],
        }
    }

    componentDidMount() {
        axios.get("http://localhost:3001/propiedades/" + 0)
            .then(res => {
                const prop = res.data;
                const graf2 = prop.graf2;
                this.setState({ graf2: graf2 })
                const materiales = [], porcentajes = [];

                this.state.graf2.map(item => {
                    materiales.push(item.material);
                    porcentajes.push(item.porcentaje);
                })

                this.setState(
                    {
                        materiales: materiales,
                        porcentajes: porcentajes
                    }
                )
            })
    }



    render() {
        console.log(this.state.mat)
        console.log(this.state.materiales)
        console.log(this.state.porcentajes)
        console.log(this.state.colores)


        const data = {
            datasets: [{
                label: 'Produccion de Papas',
                backgroundColor: 'rgba(0,255,0,1)',
                borderColor: 'black',
                borderWidth: 1,
                hoverBackgroundColor: 'rgba(0,255,0,0.2)',
                hoverBorderColor: '#FF0000',
                data: this.state.porcentajes

            }],
            labels: this.state.materiales
        };


        const opciones = {
            maintainAspectRatio: false,
            responsive: true
        }
        return (
            <>
                <div style={{ width: "100%", height: "500px" }}>
                    <h1> Pie graf</h1>
                    <Bar data={data} options={opciones} />
                </div>
            </>
        )
    }
}
