import React, { Component} from 'react'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import './css/Servicios.css'
import axios from 'axios'
import { Link } from "react-router-dom";
import { Card, Paper, Button, CardContent, CardMedia, Typography } from '@material-ui/core';


export default class Servicios extends Component {
    state = {
        prop: []
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {
        return (
            <>
                <Carousel>
                    {this.state.prop.map((item) => {
                        return (
                            <div key={item.id}>
                                <img src={item.imgBanner} />
                                <Link to={"/propiedad/" + item.id}><Button className="legend">{item.tittle}</Button></Link>
                            </div>
                        )
                    })}
                </Carousel>
            </>
        )}

}
