import React, { Component } from 'react';
import { useParams } from "react-router-dom";
import axios from 'axios'
import Map from '../components/MapTest'

export default class Region extends Component {
    constructor(props) {
        super(props);
        this.state = {
            reg: [],
            value: 'Todo'
        };
    }

    componentDidMount() {
        axios.get('http://localhost:3001/regiones')
            .then(res => {
                const reg = res.data;
                this.setState({ reg });
            })
    }

    handleChange = event => {
        this.setState({ value: event.target.value });
    };

    render() {
        const { reg } = this.state
        const { value } = this.state
        return (
            <div>

                <label>
                    Filtrar por Categorï¿½a:
                    <select value={this.state.value} onChange={this.handleChange}>
                        <option value="Todo">Todo</option>
                        <option value="1">Venta</option>
                        <option value="2">Arriendo</option>
                    </select>
                </label>

                <Use
                    list={reg}
                    val={value}
                />
            </div>
        )
    }
}

function Use(props) {
    let { id } = useParams();
    const zoomR = 8
    const heightR = 500

    return (
        <div>
            {props.list.map(item => {
                if (id === item.region_id) {
                    if (item.category === this.state.value) {
                        return (
                            <div key={item.region_id}>
                                <Map
                                    position={item.coordenadasR}
                                    zoom={zoomR}
                                    height={heightR}
                                    idParams={id}
                                />
                            </div>
                        )
                    }
                    if ("Todo" === this.state.value) {
                        return (
                            <div key={item.region_id}>
                                <Map
                                    position={item.coordenadasR}
                                    zoom={zoomR}
                                    height={heightR}
                                    idParams={id}
                                />
                            </div>
                        )
                    }
                }
            })}
        </div>  
    );
}
