import React, { Component } from 'react';
import axios from 'axios'
import { Card, Paper, Grid } from '@material-ui/core';
import './css/Propiedad.css'
import FormContact from '../components/FormContact/FormContact'
import CarouselGalery from '../components/CarouselGalery'
import Bar from '../components/Bar'
import Pie from '../components/Pie'
import Map from '../components/Map'


export default function Propiedad(props) {
    return (
        <>
            <Axios value={props.match.params.id} />
        </>
    )
}

class Axios extends Component {
    state = {
        prop: [],
        info: [],
        imgP: [],
        coordenadasP: []
    }

    componentDidMount() {
        axios.get("http://localhost:3001/propiedades/" + this.props.value)
            .then(res => {
                const prop = res.data;
                const info = prop.info;
                const imgP = prop.galery
                const coordenadasP = prop.coordenadasP
                this.setState(
                    {
                        prop, info, imgP, coordenadasP
                    }
                )
            })
    }


    render() {
        const { prop } = this.state
        const { info } = this.state
        const { imgP } = this.state

        const lat = this.state.coordenadasP[0]
        const lng = this.state.coordenadasP[1]

        const zoomP = 13
        const heightP = 300
        const prefijo = "PROPIEDAD"
        const text = "Consultar por la Propiedad"

        console.log("Prop:", this.state.coordenadasP)
        console.log(imgP)
        console.log(info)

        return (
            <>
                <Grid item xs={12}>
                    <Paper id="m" className='paper_prim'>
                        <h1 id="m1">{prop.tittle}</h1>
                        <h3 id="m2">{prop.direccion}</h3>
                        <h2 id="m3">{prop.price}</h2>
                    </Paper>
                </Grid>

                <Grid container>
                    <Card className='mainx1'>


                        <h1 className='paper_prim'>Informacion General</h1>

                        <h3 id="boxMapFake"> -- mapa -- </h3>
                        <Map
                            pos={this.state.coordenadasP}
                            /*lat={lat}
                            lng={lng}*/
                            zoom={zoomP}
                            popup={prop.tittle}
                        />

                        <div id="divinfo">
                            <li><span id="boldli">Tipo de propiedad: </span><span id="boldlip">{info.Tipo}</span></li>
                            <li><span id="boldli">Metros Cuadrados: </span><span id="boldlip">{info.m2}</span></li>
                            <li><span id="boldli">Precio por Metro Cuadrado : </span><span id="boldlip">${info.Precioxm2}</span></li>
                        </div>

                        <CarouselGalery galery={imgP} />

                        <div id = "pdiv">
                            <p id="pp">{prop.infoGeneral}</p>
                        </div>

                    </Card>

                    <Card className='mainx2'>
                        <h1 className='paper_prim'>Informacion adicional</h1>

                        <div id="BarP">
                            <Bar value={this.props.value} />
                        </div>
                        <div className="BarP">
                            <h1 id="centH">Composicion de la Propiedad</h1>
                            <Pie value={this.props.value} />
                        </div>

                        <FormContact
                            asunto={prop.tittle}
                            pref={prefijo}
                            encabezado={text}
                        />
                    </Card>
                </Grid>
            </>
        )
    }
}
