import React, { Component } from 'react';
import axios from 'axios'

export default class Region extends Component {

    constructor(props) {
        super(props);
        this.state = {
            prop: [],
            value: 'Todo'
        };
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }


    handleChange = event => {
        this.setState({ value: event.target.value });
    };


    render() {

        return (
            <div>
                <label>
                    Filtrar por Categor√≠a: 
                    <select value={this.state.value} onChange={this.handleChange}>
                        <option value="Todo">Todo</option>
                        <option value="1">Venta</option>
                        <option value="2">Arriendo</option>
                    </select>
                </label>

                 
                {this.state.prop.map(item => {
                    if (item.region == 10) {

                        if (item.category === this.state.value) {
                            return (
                                <div key={item.id}>
                                    <h3>{item.tittle}
                                        <li>{item.region}</li>
                                        <li>{item.category}</li>
                                    </h3>
                                </div>
                            )
                        }
                        if ("Todo" === this.state.value) {
                            return (
                                <div key={item.id}>
                                    <h3>{item.tittle}
                                        <li>{item.region}</li>
                                        <li>{item.category}</li>
                                    </h3>
                                </div>
                            )
                        }
                        
                    }
                }
                )}
            </div>
        )
    }
}
