import emailjs from "emailjs-com";
import React, { Component} from 'react';

function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_9w4onv5', 'template_3gy54ig', e.target, 'user_NprwZ3CIQSY9oF10i3uGX')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    e.target.reset()
}


export default class FormContacto extends Component{
    render() {

        const { asunto } = this.props

        return (
            <div>
                <div className="container">
                    <form onSubmit={sendEmail}>
                        <div className="row pt-5 mx-auto">
                            <div className="col-8 form-group mx-auto">
                                <input type="text" className="form-control" placeholder="Nombre y Apellido" name="name" />
                            </div>
                            <div className="col-8 form-group pt-2 mx-auto">
                                <input type="email" className="form-control" placeholder="Correo" name="email" />
                            </div>
                            <div className="col-8 form-group pt-2 mx-auto">
                                <input type="text" className="form-control" placeholder="Telefono" name="number" />
                            </div>
                            <div className="col-8 form-group pt-2 mx-auto">
                                <input type="submit" className="form-control" placeholder="Asunto" name="subject" subject="PREDEFINIDO" />
                            </div>
                            <div className="col-8 form-group pt-2 mx-auto">
                                <textarea className="form-control" id="" cols="30" rows="8" placeholder="Mensaje" name="message"></textarea>
                            </div>
                            <div className="col-8 pt-3 mx-auto">
                                <input type="submit" className="btn btn-info" value="Enviar"></input>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}
