import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup, LayersControl, LayerGroup} from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios'
import { Link } from "react-router-dom";
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import icon from 'leaflet/dist/images/marker-icon.png';
import Markers from './Markers'

let Icon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});


export default class MapReg extends Component {
    state = {
        prop: [],
        vent:[],
        arr: [],
        arriendos: 2,
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data.filter(item => item.region.includes(this.props.idParams));
                this.setState({ prop });
            })
    }

    render() {

        const { position } = this.props
        const { zoom } = this.props
        const { idParams } = this.props


        return (
            <>
                <MapContainer
                    className="map"
                    center={position}
                    zoom={zoom}
                    style={{ height: this.props.height }}
                >

                    <LayersControl position="topright">

                        <TileLayer
                            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />


                        <LayersControl.BaseLayer checked name="Todo">
                            <LayerGroup>
                                <Markers id={idParams}/>
                            </LayerGroup>
                        </LayersControl.BaseLayer>

                        <LayersControl.BaseLayer name="Ventas">
                            <LayerGroup>
                                {this.state.prop.map((item) => {
                                    if (item.category == 1 ) {
                                            return (
                                                <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                                    <Popup>
                                                        <li>{item.tittle}</li>
                                                        <Link to={"/propiedad/" + item.id}>
                                                            Ir a la Propiedad
                                                    </Link>
                                                    </Popup>
                                                </Marker>
                                            )
                                        }
                                })}
                            </LayerGroup>
                        </LayersControl.BaseLayer>

                        <LayersControl.BaseLayer name="Arriendos">
                            <LayerGroup>
                                {this.state.prop.map((item) => {
                                        if (item.category == this.state.arriendos) {
                                            return (
                                                <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                                    <Popup>
                                                        <li>{item.tittle}</li>
                                                        <Link to={"/propiedad/" + item.id}>
                                                            Ir a la Propiedad
                                                    </Link>
                                                    </Popup>
                                                </Marker>
                                            )
                                        }
                                })}
                            </LayerGroup>
                        </LayersControl.BaseLayer>

                    </LayersControl>
                </MapContainer>
            </>
        );
    }
}
