import React, { Component } from 'react';
import axios from 'axios'
import { NativeSelect, InputLabel } from '@material-ui/core';

export default class Region extends Component {

    constructor(props) {
        super(props);
        this.state = {
            prop: [],
            value: "Todo"
        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }


    handleSubmit(event) {
        alert("Your favorite flavor is: " + this.state.value);
        event.preventDefault();
    }

    handleChange = event => {
        this.setState({ value: event.target.value });
    };





    render() {
        const { prop } = this.state

        return (
            <div>

                <form onSubmit={this.handleSubmit}>
                    <label>
                        Filtrar por Categor√≠a: 
          <select value={this.state.value} onChange={this.handleChange}>
                            <option value="Todo">Grapefruit</option>
                            <option value="Venta">Lime</option>
                            <option value="Arriendo">Coconut</option>
                        </select>
                        <input type="submit" value="Submit" />
                    </label>
                    
                </form>

                {prop.map(item => {
                    if (item.region == 10) {
                        return (
                            <div key={item.id}>
                                <h3>{item.tittle}
                                    <li>{item.region}</li>
                                    <li>{item.category}</li>
                                </h3>
                                <h1>{this.state.filtro}</h1>
                            </div>
                        )}

                    }
                )}
            </div>
        )
    }
}
