import React, { Component } from 'react';
import axios from 'axios'
import { Card, Paper, Grid } from '@material-ui/core';
import './css/Propiedad.css'
import Map from '../components/Map'
import FormContact from '../components/FormContact/FormContact'
import CarouselGalery from '../components/CarouselGalery'
import { Pie } from 'react-chartjs-2';

export default class Servicios extends Component {
    constructor(props) {
        super(props);
        this.state = {
            prop: [],
            id: '',
            tittle: '',
            price: '',
            direccion: '',
            coordenadasP: [],
            imgP: [],
            infoGeneral: '',
            info: [],
            mat: [],
            materiales: [],
            porcentajes: [],
            colores: ["#28b012", "#48f985", "#bfcf12", "#7c2c7b"],
            data: { datasets: [], labels: [] },
            opciones: []
        }
    }

    getPropiedad() {
        axios.get("http://localhost:3001/propiedades/" + 0)
            .then(res => {
                const prop = res.data;
                const mat = prop.mat;
                this.setState({ mat: mat })
                const materiales = [], porcentajes = [];

                this.state.mat.map(item => {
                    materiales.push(item.material);
                    porcentajes.push(item.porcentaje);
                })

                this.setState(
                    {
                        id: prop.id,
                        tittle: prop.tittle,
                        price: prop.price,
                        direccion: prop.direccion,
                        coordenadasP: prop.coordenadasP,
                        imgP: prop.galery,
                        infoGeneral: prop.infoGeneral,
                        info: prop.info,
                        materiales: materiales,
                        porcentajes: porcentajes
                    }
                )
            })
    }

    configurarGrafica() {
        const data = {
            datasets: [{
                data: this.state.porcentajes,
                backgroundColor: this.state.colores
            }],
            labels: this.state.materiales,
        };
        const opciones = {
            responsive: true,
            maintainAspectRatio: false
        }
        this.setState({data:data,opciones:opciones})
    }

  async componentDidMount() {
        await this.getPropiedad();
        this.configurarGrafica();
    }

    render() {
        const { tittle } = this.state
        const { direccion } = this.state
        const { price } = this.state
        const { imgP } = this.state
        const { infoGeneral } = this.state
        const { info } = this.state
        const { mat } = this.state

        const { materiales } = this.state
        const { porcentajes } = this.state

        console.log(this.state.mat)
        console.log(this.state.materiales)
        console.log(this.state.porcentajes)
        console.log(this.state.colores)




        return (

            <div style={{ width: "100%", height: "500px" }}>
                <h1>Pie graf</h1>
                <Pie data={this.state.data} options={{ maintainAspectRatio: false }}/>
            </div>
        )
    }
}
