import React, { Component} from 'react';
import { Link } from "react-router-dom";
import { Paper, Grid, Button, CssBaseline, Container, CardContent, CardMedia, Typography } from '@material-ui/core';
import axios from 'axios';
import './css/Ventas.css'


export default class Ventas extends Component{

    state = {
        prop: []
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {
        return (
            <div className='root'>
                <React.Fragment>
                    <CssBaseline />
                    <Container fixed>
                        <Grid container spacing={3}>
                            <Grid item xs={12}>
                                <Paper className='paper_prim'>
                                    <h1>Propiedades en Venta</h1>
                                </Paper>
                            </Grid>
                            {this.state.prop.map(post => {
                                if (post.category == 1) {
                                    return (
                                        <Grid key={post.id} item xs={4}>
                                            <>
                                                <Paper className='paper_sec'>
                                                    <CardMedia
                                                        className='media'
                                                        image={post.imgBanner}
                                                        title="Imagen Descripcion"
                                                    />
                                                    <CardContent>
                                                        <Typography gutterBottom variant="h5" component="h2">
                                                            {post.tittle}
                                                        </Typography>
                                                        <Typography variant="body2" color="textSecondary" component="p">
                                                            {post.smallContent}
                                                        </Typography>
                                                    </CardContent>
                                                    <Link to={"/propiedad/" + post.id}>
                                                        <Button variant="contained" color="primary" >
                                                            Ver Propiedad
                                                        </Button>
                                                    </Link>
                                                </Paper>
                                            </>
                                        </Grid>    
                                    )
                                }
                            })}
                        </Grid>
                    </Container>
                </React.Fragment>
            </div>
        );
    }
}
