import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup, LayersControl, LayerGroup } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios'
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import icon from 'leaflet/dist/images/marker-icon.png';


let Icon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});


export default class Markers extends Component {
    state = {
        prop: [],
        arriendos: 2,
    }
    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data.filter(item => item.region.includes(this.props.idParams));
                this.setState({ prop });
            })
    }

    render() {

        return (
            <>
                {this.state.prop.map((item) => {
                        return (
                            <Marker key={item.id} position={item.coordenadasP} icon={Icon} >
                                <Popup>
                                    <li>{item.tittle}</li>
                                    <Link to={"/propiedad/" + item.id}>
                                        Ir a la Propiedad
                                                        </Link>
                                </Popup>
                            </Marker>
                        )
                    })
                }
            </>
            )     
    }
}
