import React, { Component } from 'react';
import { useParams } from "react-router-dom";
import axios from 'axios'
import Map from '../components/MapReg'
import { Select, Grid, MenuItem } from '@material-ui/core';
import "./css/Region.css"

export default class Region extends Component {
    constructor(props) {
        super(props);
        this.state = {
            reg: [],
            value: 'Todo'
        };
    }

    componentDidMount() {
        axios.get('http://localhost:3001/regiones')
            .then(res => {
                const reg = res.data;
                this.setState({ reg });
            })
    }

    handleChange = event => {
        this.setState({ value: event.target.value });
    };

    render() {
        const { reg } = this.state
        const { value } = this.state
        const { match } = this.props
        return (
            <div>
                <label>
                    <Grid container>
                        <Grid item xs={1}><h3 id="h"> Filtrar:</h3></Grid><Grid item xs={11}>
                            <Select id="sel" value={value} onChange={this.handleChange}>
                                <MenuItem value="Todo">Todo</MenuItem>
                                <MenuItem value="1">Venta</MenuItem>
                                <MenuItem value="2">Arriendo</MenuItem>
                            </Select>
                        </Grid>
                    </Grid>
                </label>
                <h1>{match.params.id}</h1>
                <Use
                    list={reg}
                    value={value}
                />
            </div>
        )
    }
}

function Use(props) {
    let { id } = useParams();
    const zoomR = 8
    const heightR = 500

    return (
        <div>
            {props.list.map(item => {
                if (id === item.region_id) {
                    return (
                        <div key={item.region_id}>
                            <Map
                                position={item.coordenadasR}
                                zoom={zoomR}
                                height={heightR}
                                idParams={id}
                                value={props.value}
                            />
                        </div>
                    )
                }
            })}
        </div>  
    );
}
