import React, { Component } from 'react';
import axios from 'axios'
import { NativeSelect, InputLabel } from '@material-ui/core';

export default class Region extends Component {

    constructor(props) {
        super(props);
        this.state = {
            prop: [],
            value: "coconut"
        };

        // this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:3001/propiedades')
            .then(res => {
                const prop = res.data;
                this.setState({ prop });
            })
    }

    render() {
        const { prop } = this.state

        return (
            <div>

                <InputLabel htmlFor="select">Filtrar por Categor√≠a</InputLabel>
                <NativeSelect id="select" value={this.state.filtro}>
                    <option value="10">Todo</option>
                    <option value="20">Venta</option>
                    <option value="20">Arriendo</option>
                </NativeSelect>

                {prop.map(item => {
                    if (item.region == 10) {
                        return (
                            <div key={item.id}>
                                <h3>{item.tittle}
                                    <li>{item.region}</li>
                                    <li>{item.category}</li>
                                </h3>
                                <h1>{this.state.filtro}</h1>
                            </div>
                        )}

                    }
                )}
            </div>
        )
    }
}
