import React, { Component } from 'react';
import axios from 'axios'
import { Card, Paper, Grid } from '@material-ui/core';
import './css/Propiedad.css'
import Map from '../components/Map'
import FormContact from '../components/FormContact/FormContact'
import CarouselGalery from '../components/CarouselGalery'

export default function Propiedad(props) {
        return (
            <>
                <div>
                    <Axios value={props.match.params.id} />
                </div>

            </>
        )
}

class Axios extends Component {
    state = {
        prop: [],
        id: '',
        tittle: '',
        price: '',
        direccion: '',
        coordenadasP: [],
        imgP: [],
        infoGeneral: ''
    }

    componentDidMount() {
        axios.get("http://localhost:3001/propiedades/" + this.props.value)
            .then(res => {
                const prop = res.data
                this.setState(
                    {
                        id: prop.id,
                        tittle: prop.tittle,
                        price: prop.price,
                        direccion: prop.direccion,
                        coordenadasP: prop.coordenadasP,
                        imgP: prop.galery,
                        infoGeneral: prop.infoGeneral
                    }
                )
            })
    }


    render() {
        const { tittle } = this.state
        const { direccion } = this.state
        const { price } = this.state
        const { imgP } = this.state


        const { coordenadasP } = this.state
        const zoomP = 13
        const heightP = 300
        const prefijo = "PROPIEDAD"
        const text = "Consultar por la Propiedad"

        const lat = coordenadasP[0]
        const lng = coordenadasP[1]
        const position = [lat, lng]

        console.log(coordenadasP)
        console.log(position)

        console.log(coordenadasP[0]);
        console.log(coordenadasP[1]);

        console.log(lat);
        console.log(lng);

        return (
            <>
                <Grid item xs={12}>
                    <Paper id="m" className='paper_prim'>
                        <h1 id="m1">{tittle}</h1>
                        <h3 id="m2">{direccion}</h3>
                        <h2 id="m3">{price}</h2>
                    </Paper>
                </Grid>

                <Grid container>
                    <Card className='mainx1'>


                        <h1 className='paper_prim'>Informacion General</h1>

                        <h3> -- mapa -- </h3>
                        {/* <Map
                        lat={lat}
                        lng={lng}
                        zoom={zoomP}
                        popup={tittle}
                        />*/}



                        <CarouselGalery galery={imgP} />

                        <FormContact
                            asunto={tittle}
                            pref={prefijo}
                            encabezado={text}
                        />

                    </Card>
                    <Card className='mainx2'>
                        <h1 className='paper_prim'>Informacion adicional</h1>

                    </Card>
                </Grid>
            </>
        )
    }
}
