import React, { Component} from 'react';
import { Link } from "react-router-dom";
import { Paper, Grid, Button, CssBaseline, Container, CardContent, CardMedia, Typography } from '@material-ui/core';
import './Cards.css'


export default class Cards extends Component{
    render() {
        const { list, render } = this.props
        const { valor } = this.props
        const { tipo } = this.props
        return (
            <div className='root'>
                <React.Fragment>
                    <CssBaseline />
                    <Container fixed>
                        <Grid container spacing={3}>
                            <Grid item xs={12}>
                                <Paper className='paper_prim'>
                                    <h1>Propiedades en { tipo } </h1>
                                </Paper>
                            </Grid>
                            {list.map((item, index) => {

                                if (render) {
                                    return render(item, index)
                                }

                                if (item.category == valor) {
                                    return (
                                        <Grid key={item.id} item xs={4}>
                                            <>
                                                <Paper className='paper_sec'>
                                                    <CardMedia
                                                        className='media'
                                                        image={item.imgBanner}
                                                        title="Imagen Descripcion"
                                                    />
                                                    <CardContent>
                                                        <Typography gutterBottom variant="h5" component="h2">
                                                            {item.tittle}
                                                        </Typography>
                                                        <Typography variant="body2" color="textSecondary" component="p">
                                                            {item.smallContent}
                                                        </Typography>
                                                    </CardContent>
                                                    <Link to={"/propiedad/" + item.id}>
                                                        <Button variant="contained" color="primary" >
                                                            Ver Propiedad
                                                        </Button>
                                                    </Link>
                                                </Paper>
                                            </>
                                        </Grid>
                                    )
                                }
                            })}
                        </Grid>
                    </Container>
                </React.Fragment>
            </div>
        );
    }
}
