import React, { Component } from 'react'
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import './Servicios.css'

export default class Servicios extends Component {

    constructor(props) {
        super(props)
        this.state = {
            offset: 0,
            tablaProp: [],
            orgtableData: [],
            cantidadP: 11,
            paginaInicial: 0
        }
        this.handlePageClick = this.handlePageClick.bind(this);
    }

    handlePageClick = (e) => {
        const selectedPage = e.selected;
        const offset = selectedPage * this.state.cantidadP;

        this.setState({
            paginaInicial: selectedPage,
            offset: offset
        }, () => {
            this.loadMoreData()
        });

    };

    loadMoreData() {
        const data = this.state.orgtableData;

        const slice = data.slice(this.state.offset, this.state.offset + this.state.cantidadP)
        this.setState({
            pageCount: Math.ceil(data.length / this.state.cantidadP),
            tablaProp: slice
        })

    }

    componentDidMount() {
        this.getData();
    }

    getData() {
        axios.get(`https://jsonplaceholder.typicode.com/comments`)
            .then(res => {
                const prop = res.data;
                const slice = prop.slice(this.state.offset, this.state.offset + this.state.cantidadP)

                this.setState({
                    pageCount: Math.ceil(prop.length / this.state.cantidadP),
                    orgtableData: res.data,
                    tablaProp: slice
                })
            });
    }

    render() {
        return (
            <div>
                <h1>Welcome to GKTechy!</h1>

                <table border="1">
                    <thead>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Body</th>

                    </thead>
                    <tbody>
                        {
                            this.state.tablaProp.map((tdata, i) => (
                                <tr>
                                    <td>{tdata.id}</td>
                                    <td>{tdata.name}</td>
                                    <td>{tdata.email}</td>
                                    <td>{tdata.body}</td>
                                </tr>

                            ))
                        }

                    </tbody>
                </table>

                <ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={this.state.pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={this.handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"} />

            </div>
        )
    }
}
