import React, { Component} from 'react';
import { Link } from "react-router-dom";
import { Paper, Grid, Button, CssBaseline, Container, CardContent, CardMedia, Typography } from '@material-ui/core';
import '../pages/css/Ventas.css'


export default class Ventas extends Component{
    render() {
        const { list, render } = this.props
        return (
            <div className='root'>
                <React.Fragment>
                    <CssBaseline />
                    <Container fixed>
                        <Grid container spacing={3}>
                            <Grid item xs={12}>
                                <Paper className='paper_prim'>
                                    <h1>Propiedades en Venta</h1>
                                </Paper>
                            </Grid>
                            {list.map((post, index) => {
                                if (render) {
                                    return render(post, index)
                                }
                                return (
                                        <Grid key={post.id} item xs={4}>
                                            <>
                                                <Paper className='paper_sec'>
                                                    <CardMedia
                                                        className='media'
                                                        image={post.imgBanner}
                                                        title="Imagen Descripcion"
                                                    />
                                                    <CardContent>
                                                        <Typography gutterBottom variant="h5" component="h2">
                                                            {post.tittle}
                                                        </Typography>
                                                        <Typography variant="body2" color="textSecondary" component="p">
                                                            {post.smallContent}
                                                        </Typography>
                                                    </CardContent>
                                                    <Link to={"/propiedad/" + post.id}>
                                                        <Button variant="contained" color="primary" >
                                                            Ver Propiedad
                                                        </Button>
                                                    </Link>
                                                </Paper>
                                            </>
                                        </Grid>    
                                    )
                            })}
                        </Grid>
                    </Container>
                </React.Fragment>
            </div>
        );
    }
}
